#include <stdio.h>

int main()
{
    int cgpa[] = {1, 2, 3};

    printf("The value of a[0] for cgpa is : %d\n", cgpa[0]);
    printf("The value of a[1] for cgpa is : %d\n", cgpa[1]);
    printf("The value of a[2] for cgpa is : %d\n", cgpa[2]);

    float marks[] = {2.3, 3.4, 4.5};

    printf("The value of a[0] marks is : %.3f\n", marks[0]);
    printf("The value of a[1] marks is : %.3f\n", marks[1]);
    printf("The value of a[2] marks is : %.3f\n", marks[2]);

    return 0;
    
}
// int main
