//? Unary Operator
public class Unary_Operator
{
    public static void main(String[] args)
    {
        int a=12;
        System.out.println(a);  
        int b=++a;
        System.out.println(b);
        int f=23;       
        int c=f++;
        System.out.println(c);
        int g=34;
        int d=--g;  
        System.out.println(d);
        int h=45;
        int e=h--;
        System.out.println(e);
        }
}